    public function countNotificacionesNuevasDeUsuario($usuario_id)
    {
      $q = $this->addNotificacionesPorUsuarioQuery($usuario_id);
      
      $alias = $q->getRootAlias();
      
      $q -> andWhere($alias.'.leido = 0');
         
      return $q->count();
    }
    
